"use strict";
// Question-9: Develop a program that determines the day of the week. Ask the user for a number (1-7)
// and use nested if statemnts to print the corresponding day's name.
let day = 2;
if (day == 1) {
    console.log("Monday");
}
if (day == 2) {
    console.log("Tuesday");
}
if (day == 3) {
    console.log("Wednesday");
}
if (day == 4) {
    console.log("Thursday");
}
if (day == 5) {
    console.log("Friday");
}
if (day == 6) {
    console.log("Saturday");
}
if (day == 7) {
    console.log("Sunday");
}
